import { ref, watchEffect } from "vue";
export default {
  __name: 'chfComponent',

  setup(__props, {
    expose
  }) {
    expose();
    const chf = ref(0);
    let euConvert = ref(0);
    let usConvert = ref(0);
    let isNotNull = ref(false);
