      console.log(chfChangeRate);
      console.log(chfChangeRate.chf.eur);
      const euChangeRate = chfChangeRate.chf.eur.toFixed(2);
      const usChangeRate = chfChangeRate.chf.usd.toFixed(2);
      console.log(euChangeRate);
      console.log(usChangeRate);
      watchEffect(() => {
        console.log(chf.value);

        if (chf.value > 0) {
          isNotNull.value = true;
          euConvert.value = chf.value * euChangeRate;
          usConvert.value = chf.value * usChangeRate;
        } else {
          isNotNull.value = false;
        }
      });
    });
    const __returned__ = {
      chf,
      euConvert,
      usConvert,
      isNotNull,
      ref,
      watchEffect
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
